CREATE TABLE IF NOT EXISTS tbl_tulisan
(
    tulisan_id            UUID               DEFAULT uuid_generate_v4() NOT NULL,
    tulisan_judul         varchar(100)       DEFAULT NULL,
    tulisan_isi           text,
    tulisan_tanggal       timestamp NULL     DEFAULT CURRENT_TIMESTAMP,
    tulisan_kategori_id   int                DEFAULT NULL,
    tulisan_kategori_nama varchar(30)        DEFAULT NULL,
    tulisan_views         int                DEFAULT '0',
    tulisan_gambar        varchar(40)        DEFAULT NULL,
    tulisan_pengguna_id   int                DEFAULT NULL,
    tulisan_author        varchar(40)        DEFAULT NULL,
    tulisan_img_slider    int       NOT NULL DEFAULT '0',
    tulisan_slug          varchar(200)       DEFAULT NULL,
    PRIMARY KEY (tulisan_id)
);

CREATE INDEX tulisan_kategori_idx ON tbl_tulisan (tulisan_kategori_id);
CREATE INDEX tulisan_pengguna_idx ON tbl_tulisan (tulisan_pengguna_id);

