CREATE TABLE IF NOT EXISTS tbl_tulisan
(
    id           UUID               DEFAULT uuid_generate_v4() NOT NULL,
    judul        varchar(100)       DEFAULT NULL,
    isi          text,
    author       varchar(100)       DEFAULT NULL,
    views        int                DEFAULT '0',
